CREATE TABLE recipes (
id INTEGER PRIMARY KEY AUTOINCREMENT,
description VARCHAR(200),
title TEXT NOT NULL,
body TEXT,
prep_time TEXT,
cooking_time TEXT,
ingredients TEXT,
instructions TEXT,
servings TEXT,
image_url TEXT,
slug TEXT UNIQUE,
difficulty TEXT,
dietary TEXT,
tags TEXT
);

